import { BuryPoint } from './track.min.js';

// 导出一个默认对象
var index = {
    // 安装插件
    install(Vue, configs) {
        const Track = new BuryPoint(configs);
        alert();
        Vue.directive('burypoint', {
            // 在绑定时执行
            beforeMount: function (el, binding) {
                Track.handleBindEvent(el, binding);
            },
        });
    },
};

export { index as default };
